class Pathfinder:
    def __init__(
            self, 
            matrix, 
            cell_width, 
            cell_height
        ):
        self.matrix = matrix
        self.grid = Grid(matrix=matrix)
        self.cell_width = cell_width
        self.cell_height = cell_height

        self.path = []
        self.enemy = None
        self.target = None

    def set_groups(self, enemy, target):
        self.enemy = pygame.sprite.GroupSingle(enemy)
        self.target = pygame.sprite.GroupSingle(target)

    def create_path(self):
        # the enemy position
        if not self.enemy or not self.target: return
        
        start_x, start_y = self.enemy.sprite.get_coord(self.cell_width, self.cell_height)
        start_x = max(0, min(start_x, len(self.grid.nodes[0]) - 1))
        start_y = max(0, min(start_y, len(self.grid.nodes) - 1))

        start = self.grid.node(start_x, start_y)

        # the nereast tower/artillery position
        end_x, end_y = self.target.sprite.get_coord(self.cell_width, self.cell_height)
        end = self.grid.node(end_x, end_y)

        finder = AStarFinder(diagonal_movement=DiagonalMovement.always)
        self.path, _ = finder.find_path(start, end, self.grid)
        self.grid.cleanup()

        return self.path
        #self.enemy.sprite.set_path(self.path)

    def draw_collision_rects(self, win):
        """Draw the collision rectangles of the path nodes on the window."""
        if self.path:
            for point in self.path:
                # Calculate the screen position from the grid node
                x = point.x * self.cell_width + self.cell_width // 2
                y = point.y * self.cell_height + self.cell_height // 2

                # Draw a small rectangle for visualization
                pygame.draw.rect(win, (255, 0, 0), (x - 2, y - 2, 4, 4))